import React, { useState, useEffect } from "react";
import { Select, MenuItem } from "@material-ui/core";
import BookList from "./BookList";
import "../styles/SearchBooks.css";

function SearchBooks() {
  const [menu, setMenu] = useState([]);
  const [books, setBooks] = useState([]);
  const [category, setCategory] = useState(null);

  useEffect(async () => {
    try {
      const url =
        "https://api.nytimes.com/svc/books/v3/lists/names.json?api-key=OGDK7aGVDlAT6L8KaYnfASlYi6ydHveG";
      const response = await fetch(url);
      if (!response.ok) {
        throw Error(response.statusText);
      }
      const data = await response.json();
      setMenu(data.results);
    } catch (error) {
      console.error(error);
    }
  }, []);

  useEffect(async () => {
    try {
      const url = `https://api.nytimes.com/svc/books/v3/lists/current/${category}.json?api-key=OGDK7aGVDlAT6L8KaYnfASlYi6ydHveG`;
      const response = await fetch(url);
      if (!response.ok) {
        throw Error(response.statusText);
      }
      const data = await response.json();

      setBooks(data.results.books);
    } catch (error) {
      console.error(error);
    }
  }, category);

  return (
    <React.Fragment>
      <form className="form" onSubmit={handleSubmit}>
        <Select
          className="select"
          labelId="category"
          id="category"
          value={category}
          onChange={() => setCategory(e.target.value)}
        >
          {menu.map(({ list_name_encoded, display_name }) => (
            <MenuItem value={list_name_encoded} key={list_name_encoded}>
              {display_name}
            </MenuItem>
          ))}
        </Select>
        <button className="button" type="submit">
          Search
        </button>
      </form>
      <BookList books={books} />
    </React.Fragment>
  );
}
export default SearchBooks;
