import React, {useReducer, useState} from "react";
import { makeStyles, withStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";

const useStyles = makeStyles({
  root: {
    minWidth: 275,
    marginBottom: 12,
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});

const reducer = (state, action) => {
  switch (action.type) {
    case 'SELECTED':
      return state.map(item => {
        if (item.id === action.id) {
          return { ...item, selected: true };
        } else {
          return item;
        }
      });
    case 'UNSELECTED':
      return state.map(item => {
        if (item.id === action.id) {
          return { ...item, selected: false };
        } else {
          return item;
        }
      });
    default:
      return state;
  }
};
function BooksList ({books, classes}) {

    //const inputItems = [];
    //const inputItems = books.map(book => (return {...book, selected: false }));
    const inputItems = books.map((book) => {return {...book, selected:false}});
    const [items, dispatch] = useReducer(reducer, inputItems);


    const handleChange = item => {
      dispatch({
        type: item.selected ? 'UNSELECTED' : 'SELECTED',
        id: item.primary_isbn10,
      });
    }
    console.log('********************');
    console.log(`Books: ${books}`);
    console.log(`Items: ${items}`);
    console.log(`inputItems: ${inputItems}`);
    console.log('********************');

    return (
      <React.Fragment>
        {inputItems.map((book, i) => (
          <Card key={i} className={classes.root} style={{ margin: "10px" }}>
            <CardContent>
              <input
                type="checkbox"
                checked={book.selected}
                onChange={() => handleChange(book)}
              />
              <Typography className={classes.title} color="textPrimary">
                {i + 1}. {book.title}
              </Typography>
              <Typography className={classes.pos} color="textSecondary">
                {book.contributor}
              </Typography>
              <Typography variant="body2" component="p">
                {book.description}
              </Typography>
            </CardContent>
          </Card>
        ))}
      </React.Fragment>
    );
}
export default withStyles(useStyles)(BooksList);
